TimelineEditorUtil = {}

function TimelineEditorUtil.GetEventLineConfig(fileName)
    local config = require("Config.Battle.TimelineEvent."..fileName)
    return config
end

function TimelineEditorUtil.GetRootMotionDatas(fileName)
    local config = require("Runtime.Battle.Config.RMAnimatorConfig.RM.RootMotionConfig_"..fileName)
    local copyConfig = {}
    for i, v in pairs(config) do
        local rootMotionData = {}
        local name = i
        for i, v in ipairs(v) do
            local frameData = {}
            frameData.x = string.format("%.4f", to_number(v.x))
            frameData.y = 0
            frameData.z = string.format("%.4f", to_number(v.z))
            frameData.rotationY = string.format("%.4f", to_number(v.y))
            table.insert(rootMotionData,frameData)
        end
        copyConfig[name] = rootMotionData
    end
    local rmCfg = {}
    rmCfg.rootMotionConfig = copyConfig
    return copyConfig
end