local AIAction = require("Runtime.Plugins.AIDesigner.Base.AITask").AIAction

---获取整数随机数
---@class SystemAI.IntRandom:AIAction
---@field min AIVar|Int
---@field max AIVar|Int
---@field inclusiveMax AIVar|Boolean
---@field storeResult AIVar|Int
local IntRandom = AIUtil.class("IntRandom", AIAction)

function IntRandom:OnUpdate()
    local left = self.min:GetValue()
    local right = self.max:GetValue()

    if self.inclusiveMax:GetValue() then
        right = right + 1
    end
    self.storeResult:SetValue(self.context:Random(left, right))

    return AITaskState.Success
end


return IntRandom