function MsgCmd_GetActiveRewardReply:Execute(data)
	---不在BattlePass期间不显示经验
	if (not BllMgr.GetBattlePassBLL():IsInBattlePass()) or (not SysUnLock.IsUnLock(X3_CFG_CONST.SYSTEM_UNLOCK_BATTLEPASS)) then
		for k, v in pairs(data.RewardList) do
			if v.Type == X3_CFG_CONST.ITEM_TYPE_BATTLEPASS_EXP then
				data.RewardList[k] = nil
			end
		end
	end
	UICommonUtil.ShowRewardPopTips(data.RewardList,2,nil,nil,X3_CFG_CONST.TASKREWARDSTACK)
end

return MsgCmd_GetActiveRewardReply
