local AICondition = require("Runtime.Plugins.AIDesigner.Base.AITask").AICondition

---Category:Date/Common
---@class CheckIsPaused:AICondition
local CheckIsPaused = class("CheckIsPaused", AICondition)

---@return AITaskState
function CheckIsPaused:OnUpdate()
    local controlEnable = GamePlayMgr.GetController():GetControlEnable()
    if not controlEnable then
        return AITaskState.Success
    end
    return AITaskState.Failure
end

return CheckIsPaused