local BaseAIAction = require("PureLogic.AI.Task.Miao.MiaoBaseAIAction")
--- 生成一个随机数
---Category:Miao
---@class GenerateRandomValue:MiaoBaseAIAction
---@field randomValue AIVar | Int
---@field randomMin Int
---@field randomMax Int
local GenerateRandomValue = class("GenerateRandomValue",BaseAIAction)

function GenerateRandomValue:OnEnter()

    local min = self.randomMin
    local max = self.randomMax
    if max < min then
        max = min + 1
    end

    local random = self.entity:Random(min,max)
    self.randomValue:SetValue(random)
end

function GenerateRandomValue:OnUpdate()
    return AITaskState.Success
end

return GenerateRandomValue