    if state == CatCardConst.StealState.PREFAIL or state == CatCardConst.StealState.CANSTEAL then
        local thinkTime = self.bll:GetStealWaitingTime()
        action_list = {
            [1] = {
                ActionType = CatCardConst.ActionType.WaitTime,
                Params = { thinkTime }
            },
            [2] = {
                ActionType = CatCardConst.ActionType.ChangeDialogueState,
                Params = { CatCardConst.DialogueState.StealCardThinking }
            },
            [3] = {
                ActionType = CatCardConst.ActionType.StealPress,
                Params = spAction.args
            },
            [4] = {
                ActionType = CatCardConst.ActionType.StealEnd,
            },
        }
    elseif state == CatCardConst.StealState.STEALCAUGHTENEMYGETCARD then
        action_list = {
            [1] = {
                ActionType = CatCardConst.ActionType.StealSuccess,
            },
            [2] = {
                ActionType = CatCardConst.ActionType.ChangeDialogueState,
                Params = { CatCardConst.DialogueState.StealCardFailCurrent }
            },
            [3] = {
                ActionType = CatCardConst.ActionType.EnemySteal,
            },
            [4] = {
                ActionType = CatCardConst.ActionType.ChangeDialogueState,
                Params = { CatCardConst.DialogueState.StealCardFailManGetCard },
            },
            [5] = {
                ActionType = CatCardConst.ActionType.StealEnd,
            }
        }
    elseif state == CatCardConst.StealState.STEALSUCCESS then
        action_list = {
            [1] = {
                ActionType = CatCardConst.ActionType.StealSuccess,
            },
            [2] = {
                ActionType = CatCardConst.ActionType.ChangeDialogueState,
                Params = { CatCardConst.DialogueState.StealCardSuccess }
            },
            [3] = {
                ActionType = CatCardConst.ActionType.StealEnd,
            },
        }
    elseif state == CatCardConst.StealState.STEALCAUGHTENEMYFULLCARD then
        action_list = {
            [1] = {
                ActionType = CatCardConst.ActionType.StealSuccess,
            },
            [2] = {
                ActionType = CatCardConst.ActionType.ChangeDialogueState,
                Params = { CatCardConst.DialogueState.StealCardFailManCardFull }
            },
            [3] = {
                ActionType = CatCardConst.ActionType.StealEnd,
            },
        }
    end
    if self.bll:IsDebugMode() then
        self.bll:LogFormat("解析数据结果：state = %s,[%s]", state, table.dump(action_list, "StealDataParser"))
    end
    order = BaseDataParser.ParseActionList(self, action_list, order, player_type)
    return order
end

