local CatCardConst = require("Runtime.System.X3Game.Modules.CatCard.Data.CatCardConst")
local BaseState = require(CatCardConst.BASE_STATE_PATH)
local EndState = class("EndState",BaseState)

function EndState:Execute()
    self:SetIsRunning(true)
    self:ShowRealCards()
    self.bll:ClearDialogue()
    self:CheckToAnotherRound()
end

--一轮结束以后，尝试进入下一轮。或者直接结束
function EndState:CheckToAnotherRound()
    self.bll:CheckAction(CatCardConst.SpecialType.ROUND_END)
end
return EndState