local DialogueTextUtil = class("DialogueTextUtil")

---返回剧情文本
---@param system DialogueSystem
---@param dialogueId int
---@param dialogueEntry DialogueEntry
---@return string
function DialogueTextUtil.GetDialogueText(system, dialogueId, dialogueEntry)
    local text = nil
    local data = LuaCfgMgr.Get("Dialogue", dialogueId, dialogueEntry.uniqueID)
    if data then
        local languageID = data.DialogueText
        if languageID ~= 0 then
            text = UITextHelper.GetUIText(languageID)
        end
    end

    return text
end

