local Memento = require("Runtime.System.X3Game.Modules.Photo.Memento.Base.Memento")

local Originator = class("Originator")

---设置备忘信息
function Originator:SetInfo(state)
    self.info = state
end

---获取当前备忘信息
function Originator:GetInfo()
    return self.info
end

---保存信息至备忘录
function Originator:SaveInfoToMemento()
    local memento = Memento:new()
    memento:SetContent(self.info)
    return memento
end

---从备忘录中获取备忘信息
function Originator:GetInfoFromMemento(memento)
    self.info = memento:GetContent()
end

return Originator