---@class PushExplore:PushBase
local PushExplore = class("PushExplore", require("Runtime.System.X3Game.Modules.Push.Action.PushBase"))

function PushExplore:IsCanPush()
    -- SystemSettingType.xlsx
    ---推送模块-挂机领奖推送，0：开启；1：关闭；默认开启
    if BllMgr.GetSystemSettingBLL():GetSettingData(X3_CFG_CONST.SYSTEM_SETTING_TYPE_POST_HANGUP) == 0 then
        return true
    end
    return false
end

function PushExplore:Register()
    local checkRewardTime = BllMgr.GetHangUpBLL():GetCheckRewardTime()
    if checkRewardTime then
        self:AddOnce(Define.PushType.PUSH_TYPE_HANGUP, checkRewardTime)
    end
end

return PushExplore