local PushBirthday = class("PushBirthday", require("Runtime.System.X3Game.Modules.Push.Action.PushBase"))

function PushBirthday:IsCanPush()
    -- 检查生日活动系统是否解锁
    local isSysUnlock = BllMgr.GetPlayerBirthdayBLL():CheckIfSysUnlock()
    if not isSysUnlock then return false end
    
    return true
end

function PushBirthday:Register()
