---@class PushStaminaGift:PushBase
local PushStaminaGift = class("PushStaminaGift", require("Runtime.System.X3Game.Modules.Push.Action.PushBase"))

function PushStaminaGift:IsCanPush()
    ---推送模块-领取体力补给，0：开启；1：关闭；默认开启
    if BllMgr.GetSystemSettingBLL():GetSettingData(X3_CFG_CONST.SYSTEM_SETTING_TYPE_POST_STAMINAGIFT) == 0 then
        return true
    end
    return false
end

function PushStaminaGift:Register()
    ---@type _date[]
    local staminaTimes = BllMgr.GetWelfareBLL():GetStaminaTimeStartTime()
    if staminaTimes then
        for k, v in pairs(staminaTimes) do
            if v then
                self:Add(Define.PushType.PUSH_TYPE_STAMINAGIFT, v)
            end
        end
    end
end

return PushStaminaGift