local SensitiveWordDefine = {}

function SensitiveWordDefine.GetLangName()
    local region = Locale.GetRegion()
    local langName = nil
    if region == Locale.Region.ChinaMainland then
        langName = Locale.GetLangName(Locale.Language.ZH_CN)
    elseif region == Locale.Region.ChinaOther then
        langName = Locale.GetLangName(Locale.Language.ZH_TW)
    elseif region == Locale.Region.SouthKorea then
        langName = Locale.GetLangName(Locale.Language.EN_US)
    elseif region == Locale.Region.Japan then
        langName = Locale.GetLangName(Locale.Language.EN_US)
    elseif region == Locale.Region.EuropeAmericaAsia then
        langName = Locale.GetLangName(Locale.Language.EN_US)
    end
    return langName
end

return SensitiveWordDefine