---@class TimelineUtil
local TimelineUtil = class("TimelineUtil")

---@param auxData CS.Timeline.TrackExtData
---@return TrackBindRoleType
function TimelineUtil:GetBindRoleTypeByTrackExtData(auxData)
    if auxData.bindSuitName and auxData.bindSuitName ~= "" then
        if string.startswith(auxData.bindSuitName, "PL") then
            return TrackBindRoleType.Female   -- 女主
        else
            return TrackBindRoleType.Male   -- 男主
        end
    elseif auxData.TopParentName and auxData.TopParentName == "" then
        return TrackBindRoleType.Monster   -- 怪物
    end
    return TrackBindRoleType.None
end

return TimelineUtil
